@use 'abstracts' as *;

*,
*::before,
*::after {
  margin: 0;
  padding: 0;
  box-sizing: inherit;
}

html {
  font-size: 62.5%;
  box-sizing: border-box;

  @media only screen and (max-width: $bp-largest) {
    font-size: 50%;
  }

  @media only screen and (max-width: $bp-large) {
    font-size: 45%;
  }

  @media only screen and (max-width: $bp-medium) {
    font-size: 40%;
  }
}

.container {
  font-family: "Lato", sans-serif;
  display: grid;
  grid-template-rows: 95vh repeat(6, min-content);
  grid-template-columns:
    minmax(6rem, 1fr) [main-start] repeat(8, minmax(min-content, 14rem))
    [main-end] minmax(6rem, 1fr);
  padding: 3rem;
  background-color: $color-grey-light-1;

  @media only screen and (max-width: $bp-large) {
    // grid-template-columns:
    //   minmax(6rem, 1fr) [main-start] repeat(4, minmax(min-content, 14rem))
    //   [main-end] minmax(6rem, 1fr);
    padding: 0;
  }
}

