@use 'abstracts' as *;

.heading-1 {
  text-transform: uppercase;
  font-size: 8rem;
  font-weight: 400;
  letter-spacing: 4rem;
  color: $color-white;
  margin-left: 4rem;
  text-align: center;

  @media only screen and (max-width: $bp-medium) {
    font-size: 6rem;
    letter-spacing: 2.5rem;
    margin-left: 2.5rem;
  }
  @media only screen and (max-width: $bp-small) {
    font-size: 4rem;
    letter-spacing: 1.5rem;
    margin-left: 1.5rem;
  }
}

.heading-2 {
  text-transform: uppercase;
  font-size: 2rem;
  font-weight: 400;
  letter-spacing: 2.4rem;
  color: $color-white;
  margin-left: 2.4rem;

  @media only screen and (max-width: $bp-medium) {
    font-size: 2rem;
    letter-spacing: 1.3rem;
    margin-left: 1.3rem;
  }

  @media only screen and (max-width: $bp-small) {
    font-size: 1.6rem;
    letter-spacing: 0.7rem;
    margin-left: 0.7rem;
  }
}

.heading-3 {
  text-transform: uppercase;
  font-size: 4rem;
  font-weight: 700;
  letter-spacing: 0.5rem;
  word-spacing: 1rem;
  background-image: linear-gradient(
    to right,
    rgba($color-primary-light, 1),
    rgba($color-primary-dark, 1)
  );
  margin-bottom: $margin-large;

  -webkit-background-clip: text;
  background-clip: text;
  -webkit-text-fill-color: transparent;

  transition: all 0.3s;

  &:hover {
    transform: skew(15deg, 2deg) scaleX(1.1);
    transform-origin: center;
    text-shadow: 0 1rem 2rem rgba(black, 0.4);
  }

  @media only screen and (max-width: $bp-small) {
    font-size: 2.5rem;
  }
}

.heading-4 {
  text-transform: uppercase;
  font-size: 1.8rem;
  font-weight: 700;
  color: $color-grey-dark;
  margin-bottom: $margin-small;
}

.paragraph {
  font-family: inherit;
  font-size: 1.6rem;
}

.primary {
  color: $color-primary-light;
}

.bold {
  font-weight: 700;
}

