@use 'abstracts' as *;

.btn {
  border: none;
  padding: 1.8rem 4rem;
  text-transform: uppercase;
  border-radius: 5rem;
  transition: all 0.2s ease-in-out;
  position: relative;
  text-decoration: none;
  font-size: 1.6rem;

  &::after {
    content: "";
    display: block;
    background-color: #fff;
    width: 100%;
    height: 100%;
    position: absolute;
    top: 0;
    left: 0;
    border-radius: 5rem;
    z-index: -1;
    opacity: 1;
    transition: all 0.4s ease-in-out;
  }

  &:hover {
    cursor: pointer;
    box-shadow: 0 1rem 2rem rgba(black, 0.2);
    transform: translateY(-3px);

    &::after {
      transform: scale(1.5);
      opacity: 0;
    }
  }

  &:active {
    box-shadow: 0 1rem 2rem rgba(black, 0);
    transform: translateY(0);
  }

  &--white {
    background-color: $color-white;
    color: $color-grey-dark;
  }

  &--green {
    background-color: $color-primary;
    color: $color-white;
  }
}

