@use 'abstracts' as *;

.story-section {
  position: relative;
  padding: 15rem 0;

  display: grid;
  justify-items: center;
  gap: 10rem;
  //   align-content: center;
}

.bg-video {
  position: absolute;
  top: 0;
  left: 0;
  height: 100%;
  width: 100%;
  opacity: 0.3;
  z-index: -1;

  &__content {
    height: 100%;
    width: 100%;
    object-fit: cover;
  }
}

.story {
  background-color: rgba($color-grey-light-1, 0.8);
  width: 60%;
  padding: 5rem;
  // transform: skewX(-15deg);
  position: relative;

  &__shape {
    height: 14rem;
    width: 14rem;
    border-radius: 50%;
    // transform: skewX(15deg);
    float: left;
    margin-right: 8rem;
    shape-outside: ellipse(35% 45% at 40% 50%);
    clip-path: circle(50%);

    @media only screen and (max-width: $bp-medium) {
      shape-outside: ellipse(45% 60% at 40% 50%);
      margin-bottom: 1rem;
    }
  }

  &__img {
    height: 100%;
    transform: translateX(-3rem) scale(1.1);
    transition: all 0.2s;
  }

  &__caption {
    color: $color-white;
    text-align: center;
    text-transform: uppercase;
    font-size: 1.6rem;
    transition: all 0.4s;
  }

  &__heading {
    // transform: skewX(15deg);
  }

  &__paragraph {
    // transform: skewX(15deg);
    padding: 1rem;
  }

  &:hover &__img {
    transform: translateX(-3rem) scale(1);
    filter: blur(3px) brightness(90%);
  }

  &:hover &__caption {
    transform: translateY(-8rem);
  }
}

