@use 'abstracts' as *;

.footer {
  background-color: $color-grey-dark;

  &__logo-box {
    display: flex;
    justify-content: center;
    padding: 5rem;
  }

  &__logo {
    height: 8rem;
    color: #DDA448;
  }

  &__content {
    display: grid;
    grid-template-columns: 1fr 40% 1fr 40% 1fr;
    margin-bottom: 10rem;
    justify-items: center;
