        left: 0;
        border-radius: 5rem;
        z-index: -1;
        opacity: 1;
        transition: all .4s ease-in-out;
    }

    &:hover {
        cursor: pointer;
        box-shadow: 0 1rem 2rem rgba(black, .2);
        transform: translateY(-3px);

        &::after {
            transform: scale(1.5);
            opacity: 0;
        }
    }

    &:active {
        box-shadow: 0 1rem 2rem rgba(black, 0);
        transform: translateY(0);
    }
    
    &--white {
        background-color: $color-white;
        color: $color-grey-dark;
    }

