    width: 50%;
    display: grid;
    grid-template-columns: repeat(2, 1fr);
    row-gap: 6rem;
    margin-bottom: 5rem;
    position: relative;

    @media only screen and (max-width: $bp-small) {
      width: 100%;
    }
