    &--name {
      top: 7.5rem;
    }

    &--email {
      top: 19rem;
    }
  }

  &__input:placeholder-shown + &__input-label {
    opacity: 0;
    visibility: hidden;

    &--name {
      top: 1.6rem;
    }
    &--email {
      top: 13rem;
    }
