    content: "";
    top: -3px;
    left: 0;
    width: 4.5rem;
    height: 4.5rem;
    border-radius: 50%;
    border: 0.8rem solid rgba($color-primary, 1);

    &::after {
      content: "";
      display: block;
      position: absolute;
      top: 50%;
      left: 50%;
      transform: translate(-50%, -50%);
      width: 2.2rem;
      height: 2.2rem;
      border-radius: 50%;
      background-color: $color-primary;
      opacity: 0;
      transition: opacity 0.2s;
    }
  }
